# Mathias, 16 June 2014# usage example:import sysdef collapse_empty_fields (infilename):		outlines = []	with open(infilename, "r") as INFILE:		for line in INFILE:			fields = line.strip("\n").split(" ")			outline = []			for field in fields:				if len(field) >= 1:					outline.append(field)			outline = "\t".join(outline)			outlines.append(outline)			with open("{}.tsv".format(infilename), "w") as OUTFILE:		OUTFILE.write("\n".join(outlines))def main(argv=None):	if argv is None:		argv = sys.argv[1:]	collapse_empty_fields(argv[0])	if __name__ == "__main__":	main(sys.argv[1:])			